enum_types:
CREATE TYPE reaction_type_enum AS ENUM ('loved', 'hated', 'interested', 'bought');
 CREATE TYPE retail_name_enum AS ENUM ('ebay', 'amazon', 'ali_express');
 CREATE TYPE connection_status_enum AS ENUM ('pending', 'follow', 'unfollow');
 CREATE TYPE notification_type_enum AS ENUM ('follow_request', 'confirmed');

reactions:
CREATE TABLE IF NOT EXISTS reactions (id serial PRIMARY KEY, retail_id VARCHAR ( 255 ) NOT NULL, retail_name retail_name_enum NOT NULL, user_id INT NOT NULL,type reaction_type_enum NOT NULL,product_name VARCHAR( 255 ) NOT NULL,product_image TEXT,product_thumbnail_image TEXT,product_price_value DOUBLE PRECISION NOT NULL, product_price_currency VARCHAR ( 255 ) NOT NULL,product_shipping_cost DOUBLE PRECISION, product_shipping_currency VARCHAR ( 255 ), item_href TEXT, active BOOLEAN NOT NULL DEFAULT true ,created_at TIMESTAMPTZ NOT NULL DEFAULT NOW(), updated_at TIMESTAMPTZ NOT NULL DEFAULT NOW(), FOREIGN KEY (retail_id, retail_name) REFERENCES products (retail_id, retail_name), FOREIGN KEY (user_id) REFERENCES users (id));
DROP TRIGGER IF EXISTS set_timestamp ON reactions CASCADE;
CREATE TRIGGER set_timestamp BEFORE UPDATE ON reactions FOR EACH ROW EXECUTE PROCEDURE trigger_set_timestamp();
CREATE UNIQUE INDEX woodie_reactions_constraint ON reactions(retail_id, retail_name, user_id)
